class Logger {
  constructor(level) {
    this.level = level;
  }

  log(message) {
    console.log(`[${this.level}] ${message}`);
  }
}

// This can be thought of as if we are doing `export { Logger as Logger }` in ESM
exports.Logger = Logger;
// This can be thought of as if we are doing `export { Logger as LoggerAlias }` in ESM
exports.LoggerAlias = Logger;
