class Logger {
  constructor(level) {
    this.level = level;
  }

  log(message) {
    console.log(`[${this.level}] ${message}`);
  }
}

const defaultLogger = new Logger('debug');
// Caveat: if module.exports is assigned, named exports in the form of
// exports.foo = bar; won't be detected.
// This can be thought of as if we are doing:
// `export { defaultLogger as default, Logger, Logger as LoggerAlias }`
// in ESM.
module.exports = defaultLogger;
module.exports.Logger = Logger;
module.exports.LoggerAlias = Logger;
